Parser::ParserReturnCode parse (std::string const &s, double &parsed_value)
{
    std::istringstream in(s);
    Parser parser;
    parser.attach_istream(in);
    Parser::ParserReturnCode return_code = parser.Parse(&parsed_value);
    return return_code;
}

